mapper.insert:
INSERT INTO MEMBER
VALUES(#{id}, #{pwd}, #{name}, #{nickname}, #{birth}, #{email},#{phone},#{gender}, #{job}
		, #{withd_yn}, SYSDATE, SYSDATE,DEFAULT)

mapper.select:
SELECT COUNT(*)
FROM MEMBER
WHERE USER_ID=#{id} AND EMAIL = #{email}

SELECT TRAV_STYLE
FROM PREF_STYLE
WHERE USER_ID=#{id}

SELECT USER_ID
FROM MEMBER
WHERE NAME=#{search}

SELECT COUNT(*)
FROM SHARED
WHERE SHARE_ID = #{id}

