mapper.select:
SELECT *
FROM MEMBER
WHERE ID=#{id} AND PWD=#{pwd}

SELECT *
FROM MEMBER
WHERE ID=#{id} AND EMAIL=#{email}

SELECT *
FROM MEMBER
WHERE NAME=#{name} AND EMAIL=#{email}

SELECT COUNT(*)
FROM MEMBER
WHERE ID=#{id}

SELECT *
FROM FRIEND_YN
WHERE FR_ID=#{userId}  AND ACCEPT_YN=#{acceptYn}

SELECT *
FROM FRIEND_YN
WHERE FR_ID=#{userId} AND USER_ID=#{fId} AND ACCEPT_YN=#{acceptYn}
